parameters.jsCode:
const normalized = [];

for (const item of $input.all()) {
  let newsItem = {
    title: '',
    url: '',
    source: '',
    content: '',
    timestamp: new Date().toISOString()
  };

  // Handle RSS Feed (Inc42, Entrackr, etc)
  if (item.json.title && item.json.link) {
    newsItem.title = item.json.title;
    newsItem.url = item.json.link;
    newsItem.source = item.json.feed?.title || 'RSS Feed';
    newsItem.content = item.json.contentSnippet || item.json.description || item.json.content || '';
  }

  // Handle Reddit
  else if (item.json.data?.children) {
    for (const child of item.json.data.children) {
      const post = child.data;
      if (post && post.title) {
        normalized.push({
          title: post.title,
          url: post.url || `https://reddit.com${post.permalink}`,
          source: 'Reddit r/IndianStartups',
          content: post.selftext || post.title,
          timestamp: new Date(post.created_utc * 1000).toISOString()
        });
      }
    }
    continue;
  }

  // Handle Hacker News
  else if (item.json.hits) {
    for (const hit of item.json.hits) {
      if (hit && hit.title) {
        normalized.push({
          title: hit.title,
          url: hit.url || `https://news.ycombinator.com/item?id=${hit.objectID}`,
          source: 'Hacker News',
          content: hit.story_text || hit.title,
          timestamp: new Date(hit.created_at).toISOString()
        });
      }
    }
    continue;
  }

  if (newsItem.title) {
    normalized.push(newsItem);
  }
}

// Remove duplicates by title
const unique = [];
const seen = new Set();

for (const item of normalized) {
  const key = item.title.toLowerCase().trim();
  if (!seen.has(key)) {
    seen.add(key);
    unique.push(item);
  }
}

// Limit to 10 items to avoid too many API calls
return unique.slice(0, 10).map(item => ({ json: item }));

